import "./Card.css";

class Card extends Component{
    constructor(props){
        super(props);
        this.state={
          users: [],
          usersTam: "" 
        };
      }
      apiCall(url, consecuencia){
        fetch(url)
          .then(res => res.json())
          .then(data => {
                console.log(data);
                this.setState(
                  {
                    users: data.users,
                    usersTam: data.cont
                  } 
                )
          })
          .catch(error => console.log(error));
      }

      componentDidMount(){
        console.log("Me monté");
        this.apiCall("http://localhost:3100/api/users", this.mostrarUsers)
      }

      componentDidUpdate(){
        console.log("Me actualicé");
      }
render(props){
