import React, {Component} from 'react';
import "./Card.css";
let estiloColor;
let estiloText;
let color;
class Card extends Component{

    constructor(props){
        super(props);
        this.state={
          users: [],
          usersTam: "",
        };

    }
      apiCall(url, consecuencia){
        fetch(url)
          .then(res => res.json())
          .then(data => {
                console.log(data);
                this.setState(
                  {
                    users: data.users,
                    usersTam: data.cont
                  } 
                )
          })
          .catch(error => console.log(error));
      }

      componentDidMount(){
        console.log("Me monté");
        this.apiCall("http://localhost:3100/api/users", this.mostrarUsers)
      }

      componentDidUpdate(){
        console.log("Me actualicé");
      }
        render(props){
            color= props.color
            estiloColor ="card border-left-"+color+" shadow h-100 py-2",
            estiloText="text-xs font-weight-bold text-"+color+" text-uppercase mb-1"
        return (
            <div className="col-xl-3 col-md-6 mb-4">

                <div className={estiloColor}>
                    <div className="card-body">
                        <div className="row no-gutters align-items-center">
                            <div className="col mr-2">
                                <div className={estiloText}>
                                    Earnings (Monthly)</div>
                                <div className="h5 mb-0 font-weight-bold text-gray-800">$40,000</div>
                            </div>
                            <div className="col-auto">
                                <i className="fas fa-calendar fa-2x text-gray-300">
                                </i>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
        }
}
  Card.defaultProps={
      color:"primary",

  }

export default Card;
